WITH customer_id AS (     SELECT t.CustomerID     FROM transactions_1k t     WHERE t.Date='2012-08-25' AND ROUND(t.Amount * t.Price, 2)=634.8 ), consumptions AS (     SELECT SUM(CASE WHEN SUBSTR(ym.Date, 1, 4)='2012' THEN ym.Consumption ELSE 0 END) AS consumption_2012,            SUM(CASE WHEN SUBSTR(ym.Date, 1, 4)='2013' THEN ym.Consumption ELSE 0 END) AS consumption_2013     FROM yearmonth ym     JOIN customer_id ci ON ym.CustomerID=ci.CustomerID ) SELECT ((consumption_2012-consumption_2013)/consumption_2012)*100 AS consumption_decrease_rate FROM consumptions;
