SELECT COUNT(DISTINCT p.ID)  FROM Examination AS e  JOIN Patient AS p ON e.ID = p.ID  WHERE EXTRACT(YEAR FROM p.Birthday) <= EXTRACT(YEAR FROM e."Examination Date") - 18  AND EXTRACT(YEAR FROM e."Examination Date") BETWEEN 1990 AND 1993;
